-- AUTH TEMPLATES

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Login Form',
  'Email and password login form with remember me checkbox and validation',
  'auth',
  'react',
  '{"files": [{"path": "LoginForm.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function LoginForm() {\n  const [email, setEmail] = useState('''');\n  const [password, setPassword] = useState('''');\n  const [remember, setRemember] = useState(false);\n\n  const handleSubmit = (e: React.FormEvent) => {\n    e.preventDefault();\n    console.log({ email, password, remember });\n  };\n\n  return (\n    <div className=\"w-full max-w-md mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-6\">Sign In</h2>\n      <form onSubmit={handleSubmit} className=\"space-y-4\">\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Email</label>\n          <input\n            type=\"email\"\n            value={email}\n            onChange={(e) => setEmail(e.target.value)}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Password</label>\n          <input\n            type=\"password\"\n            value={password}\n            onChange={(e) => setPassword(e.target.value)}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <div className=\"flex items-center\">\n          <input\n            type=\"checkbox\"\n            id=\"remember\"\n            checked={remember}\n            onChange={(e) => setRemember(e.target.checked)}\n            className=\"w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500\"\n          />\n          <label htmlFor=\"remember\" className=\"ml-2 text-sm text-gray-600\">Remember me</label>\n        </div>\n        <button\n          type=\"submit\"\n          className=\"w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors\"\n        >\n          Sign In\n        </button>\n      </form>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Signup Form',
  'User registration form with name, email, password, and password confirmation',
  'auth',
  'react',
  '{"files": [{"path": "SignupForm.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function SignupForm() {\n  const [formData, setFormData] = useState({\n    name: '''',\n    email: '''',\n    password: '''',\n    confirmPassword: ''''\n  });\n  const [error, setError] = useState('''');\n\n  const handleSubmit = (e: React.FormEvent) => {\n    e.preventDefault();\n    if (formData.password !== formData.confirmPassword) {\n      setError(''Passwords do not match'');\n      return;\n    }\n    setError('''');\n    console.log(formData);\n  };\n\n  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {\n    setFormData({ ...formData, [e.target.name]: e.target.value });\n  };\n\n  return (\n    <div className=\"w-full max-w-md mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-6\">Create Account</h2>\n      <form onSubmit={handleSubmit} className=\"space-y-4\">\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Full Name</label>\n          <input\n            type=\"text\"\n            name=\"name\"\n            value={formData.name}\n            onChange={handleChange}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Email</label>\n          <input\n            type=\"email\"\n            name=\"email\"\n            value={formData.email}\n            onChange={handleChange}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Password</label>\n          <input\n            type=\"password\"\n            name=\"password\"\n            value={formData.password}\n            onChange={handleChange}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Confirm Password</label>\n          <input\n            type=\"password\"\n            name=\"confirmPassword\"\n            value={formData.confirmPassword}\n            onChange={handleChange}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        {error && <p className=\"text-sm text-red-600\">{error}</p>}\n        <button\n          type=\"submit\"\n          className=\"w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors\"\n        >\n          Sign Up\n        </button>\n      </form>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Password Reset',
  'Password reset request form with email input and confirmation message',
  'auth',
  'react',
  '{"files": [{"path": "PasswordReset.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function PasswordReset() {\n  const [email, setEmail] = useState('''');\n  const [submitted, setSubmitted] = useState(false);\n\n  const handleSubmit = (e: React.FormEvent) => {\n    e.preventDefault();\n    console.log({ email });\n    setSubmitted(true);\n  };\n\n  if (submitted) {\n    return (\n      <div className=\"w-full max-w-md mx-auto p-6 bg-white rounded-lg shadow-md\">\n        <div className=\"text-center\">\n          <div className=\"w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4\">\n            <svg className=\"w-6 h-6 text-green-600\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n              <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M5 13l4 4L19 7\" />\n            </svg>\n          </div>\n          <h2 className=\"text-xl font-bold text-gray-900 mb-2\">Check Your Email</h2>\n          <p className=\"text-gray-600\">We''ve sent a password reset link to {email}</p>\n        </div>\n      </div>\n    );\n  }\n\n  return (\n    <div className=\"w-full max-w-md mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-2\">Reset Password</h2>\n      <p className=\"text-gray-600 mb-6\">Enter your email address and we''ll send you a reset link</p>\n      <form onSubmit={handleSubmit} className=\"space-y-4\">\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Email</label>\n          <input\n            type=\"email\"\n            value={email}\n            onChange={(e) => setEmail(e.target.value)}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <button\n          type=\"submit\"\n          className=\"w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors\"\n        >\n          Send Reset Link\n        </button>\n      </form>\n    </div>\n  );\n}"}]}',
  true,
  null
);

