INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Navbar',
  'Responsive navigation bar with logo, links, and mobile hamburger menu',
  'other',
  'react',
  '{"files": [{"path": "Navbar.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function Navbar() {\n  const [isOpen, setIsOpen] = useState(false);\n\n  return (\n    <nav className=\"bg-white shadow-md\">\n      <div className=\"max-w-7xl mx-auto px-4 sm:px-6 lg:px-8\">\n        <div className=\"flex justify-between h-16\">\n          <div className=\"flex items-center\">\n            <span className=\"text-2xl font-bold text-blue-600\">Logo</span>\n          </div>\n          <div className=\"hidden md:flex items-center space-x-8\">\n            <a href=\"#\" className=\"text-gray-700 hover:text-blue-600 transition-colors\">Home</a>\n            <a href=\"#\" className=\"text-gray-700 hover:text-blue-600 transition-colors\">About</a>\n            <a href=\"#\" className=\"text-gray-700 hover:text-blue-600 transition-colors\">Services</a>\n            <a href=\"#\" className=\"text-gray-700 hover:text-blue-600 transition-colors\">Contact</a>\n            <button className=\"bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors\">\n              Get Started\n            </button>\n          </div>\n          <div className=\"md:hidden flex items-center\">\n            <button\n              onClick={() => setIsOpen(!isOpen)}\n              className=\"text-gray-700 hover:text-blue-600\"\n            >\n              <svg className=\"w-6 h-6\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                {isOpen ? (\n                  <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M6 18L18 6M6 6l12 12\" />\n                ) : (\n                  <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M4 6h16M4 12h16M4 18h16\" />\n                )}\n              </svg>\n            </button>\n          </div>\n        </div>\n      </div>\n      {isOpen && (\n        <div className=\"md:hidden\">\n          <div className=\"px-2 pt-2 pb-3 space-y-1\">\n            <a href=\"#\" className=\"block px-3 py-2 text-gray-700 hover:bg-blue-50 rounded-md\">Home</a>\n            <a href=\"#\" className=\"block px-3 py-2 text-gray-700 hover:bg-blue-50 rounded-md\">About</a>\n            <a href=\"#\" className=\"block px-3 py-2 text-gray-700 hover:bg-blue-50 rounded-md\">Services</a>\n            <a href=\"#\" className=\"block px-3 py-2 text-gray-700 hover:bg-blue-50 rounded-md\">Contact</a>\n            <button className=\"w-full text-left px-3 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700\">\n              Get Started\n            </button>\n          </div>\n        </div>\n      )}\n    </nav>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Sidebar',
  'Collapsible sidebar navigation with nested menu items and icons',
  'other',
  'react',
  '{"files": [{"path": "Sidebar.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function Sidebar() {\n  const [isOpen, setIsOpen] = useState(true);\n  const [openSection, setOpenSection] = useState<string | null>(null);\n\n  const toggleSection = (section: string) => {\n    setOpenSection(openSection === section ? null : section);\n  };\n\n  return (\n    <div className={`${isOpen ? ''w-64'' : ''w-20''} bg-gray-900 text-white h-screen transition-all duration-300`}>\n      <div className=\"p-4 flex items-center justify-between border-b border-gray-800\">\n        {isOpen && <span className=\"text-xl font-bold\">Menu</span>}\n        <button onClick={() => setIsOpen(!isOpen)} className=\"text-gray-400 hover:text-white\">\n          <svg className=\"w-6 h-6\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n            <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M4 6h16M4 12h16M4 18h16\" />\n          </svg>\n        </button>\n      </div>\n      <nav className=\"p-4 space-y-2\">\n        <a href=\"#\" className=\"flex items-center space-x-3 p-2 rounded-md hover:bg-gray-800 transition-colors\">\n          <svg className=\"w-5 h-5\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n            <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6\" />\n          </svg>\n          {isOpen && <span>Dashboard</span>}\n        </a>\n        <div>\n          <button\n            onClick={() => toggleSection(''projects'')}\n            className=\"flex items-center justify-between w-full p-2 rounded-md hover:bg-gray-800 transition-colors\"\n          >\n            <div className=\"flex items-center space-x-3\">\n              <svg className=\"w-5 h-5\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z\" />\n              </svg>\n              {isOpen && <span>Projects</span>}\n            </div>\n            {isOpen && (\n              <svg className={`w-4 h-4 transition-transform ${openSection === ''projects'' ? ''rotate-180'' : ''''}`} fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M19 9l-7 7-7-7\" />\n              </svg>\n            )}\n          </button>\n          {isOpen && openSection === ''projects'' && (\n            <div className=\"ml-8 mt-1 space-y-1\">\n              <a href=\"#\" className=\"block p-2 text-sm text-gray-400 hover:text-white\">Active</a>\n              <a href=\"#\" className=\"block p-2 text-sm text-gray-400 hover:text-white\">Archived</a>\n            </div>\n          )}\n        </div>\n        <a href=\"#\" className=\"flex items-center space-x-3 p-2 rounded-md hover:bg-gray-800 transition-colors\">\n          <svg className=\"w-5 h-5\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n            <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z\" />\n            <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M15 12a3 3 0 11-6 0 3 3 0 016 0z\" />\n          </svg>\n          {isOpen && <span>Settings</span>}\n        </a>\n      </nav>\n    </div>\n  );\n}"}]}',
  true,
  null
);

