INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Contact Form',
  'Contact form with name, email, message fields and client-side validation',
  'other',
  'react',
  '{"files": [{"path": "ContactForm.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function ContactForm() {\n  const [formData, setFormData] = useState({\n    name: '''',\n    email: '''',\n    message: ''''\n  });\n  const [submitted, setSubmitted] = useState(false);\n\n  const handleSubmit = (e: React.FormEvent) => {\n    e.preventDefault();\n    console.log(formData);\n    setSubmitted(true);\n    setTimeout(() => {\n      setSubmitted(false);\n      setFormData({ name: '''', email: '''', message: '''' });\n    }, 3000);\n  };\n\n  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {\n    setFormData({ ...formData, [e.target.name]: e.target.value });\n  };\n\n  return (\n    <div className=\"w-full max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-6\">Contact Us</h2>\n      {submitted && (\n        <div className=\"mb-4 p-4 bg-green-50 border border-green-200 rounded-md\">\n          <p className=\"text-green-800\">Thank you! Your message has been sent.</p>\n        </div>\n      )}\n      <form onSubmit={handleSubmit} className=\"space-y-4\">\n        <div className=\"grid grid-cols-1 md:grid-cols-2 gap-4\">\n          <div>\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Name</label>\n            <input\n              type=\"text\"\n              name=\"name\"\n              value={formData.name}\n              onChange={handleChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n              required\n            />\n          </div>\n          <div>\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Email</label>\n            <input\n              type=\"email\"\n              name=\"email\"\n              value={formData.email}\n              onChange={handleChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n              required\n            />\n          </div>\n        </div>\n        <div>\n          <label className=\"block text-sm font-medium text-gray-700 mb-1\">Message</label>\n          <textarea\n            name=\"message\"\n            value={formData.message}\n            onChange={handleChange}\n            rows={5}\n            className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent\"\n            required\n          />\n        </div>\n        <button\n          type=\"submit\"\n          className=\"w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors\"\n        >\n          Send Message\n        </button>\n      </form>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Multi-step Form',
  'Three-step form wizard with progress indicator and navigation between steps',
  'other',
  'react',
  '{"files": [{"path": "MultiStepForm.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function MultiStepForm() {\n  const [step, setStep] = useState(1);\n  const [formData, setFormData] = useState({\n    name: '''', email: '''', address: '''', city: '''', card: '''', expiry: ''''\n  });\n\n  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {\n    setFormData({ ...formData, [e.target.name]: e.target.value });\n  };\n\n  const handleSubmit = () => {\n    console.log(formData);\n    alert(''Form submitted!'');\n  };\n\n  return (\n    <div className=\"w-full max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <div className=\"mb-8\">\n        <div className=\"flex items-center justify-between\">\n          {[1, 2, 3].map((s) => (\n            <div key={s} className=\"flex items-center\">\n              <div className={`w-10 h-10 rounded-full flex items-center justify-center ${\n                step >= s ? ''bg-blue-600 text-white'' : ''bg-gray-200 text-gray-600''\n              }`}>\n                {s}\n              </div>\n              {s < 3 && <div className={`w-24 h-1 ${\n                step > s ? ''bg-blue-600'' : ''bg-gray-200''\n              }`} />}\n            </div>\n          ))}\n        </div>\n        <div className=\"flex justify-between mt-2\">\n          <span className=\"text-sm text-gray-600\">Personal</span>\n          <span className=\"text-sm text-gray-600\">Address</span>\n          <span className=\"text-sm text-gray-600\">Payment</span>\n        </div>\n      </div>\n      <div className=\"space-y-4\">\n        {step === 1 && (\n          <>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">Full Name</label>\n              <input type=\"text\" name=\"name\" value={formData.name} onChange={handleChange}\n                className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">Email</label>\n              <input type=\"email\" name=\"email\" value={formData.email} onChange={handleChange}\n                className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n          </>\n        )}\n        {step === 2 && (\n          <>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">Address</label>\n              <input type=\"text\" name=\"address\" value={formData.address} onChange={handleChange}\n                className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">City</label>\n              <input type=\"text\" name=\"city\" value={formData.city} onChange={handleChange}\n                className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n          </>\n        )}\n        {step === 3 && (\n          <>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">Card Number</label>\n              <input type=\"text\" name=\"card\" value={formData.card} onChange={handleChange}\n                className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n            <div>\n              <label className=\"block text-sm font-medium text-gray-700 mb-1\">Expiry Date</label>\n              <input type=\"text\" name=\"expiry\" value={formData.expiry} onChange={handleChange}\n                placeholder=\"MM/YY\" className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500\" />\n            </div>\n          </>\n        )}\n      </div>\n      <div className=\"flex justify-between mt-6\">\n        <button onClick={() => setStep(step - 1)} disabled={step === 1}\n          className=\"px-4 py-2 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50\">\n          Previous\n        </button>\n        {step < 3 ? (\n          <button onClick={() => setStep(step + 1)}\n            className=\"px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700\">\n            Next\n          </button>\n        ) : (\n          <button onClick={handleSubmit}\n            className=\"px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700\">\n            Submit\n          </button>\n        )}\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

