INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Product Card',
  'Product card with image, title, price, rating, and add to cart button',
  'ecommerce',
  'react',
  '{"files": [{"path": "ProductCard.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\nexport default function ProductCard() {\n  const [isAdded, setIsAdded] = useState(false);\n\n  const handleAddToCart = () => {\n    setIsAdded(true);\n    setTimeout(() => setIsAdded(false), 2000);\n  };\n\n  return (\n    <div className=\"w-full max-w-sm bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow\">\n      <div className=\"relative h-64 bg-gray-200\">\n        <div className=\"absolute top-2 right-2 bg-red-500 text-white px-2 py-1 rounded-md text-sm font-semibold\">\n          -20%\n        </div>\n        <div className=\"h-full flex items-center justify-center text-gray-400\">\n          <svg className=\"w-24 h-24\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n            <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z\" />\n          </svg>\n        </div>\n      </div>\n      <div className=\"p-4\">\n        <h3 className=\"text-lg font-semibold text-gray-900 mb-2\">Premium Wireless Headphones</h3>\n        <div className=\"flex items-center mb-2\">\n          <div className=\"flex text-yellow-400\">\n            {[1, 2, 3, 4, 5].map((star) => (\n              <svg key={star} className=\"w-4 h-4 fill-current\" viewBox=\"0 0 20 20\">\n                <path d=\"M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z\" />\n              </svg>\n            ))}\n          </div>\n          <span className=\"ml-2 text-sm text-gray-600\">(128 reviews)</span>\n        </div>\n        <div className=\"flex items-baseline mb-4\">\n          <span className=\"text-2xl font-bold text-gray-900\">$79.99</span>\n          <span className=\"ml-2 text-lg text-gray-500 line-through\">$99.99</span>\n        </div>\n        <button\n          onClick={handleAddToCart}\n          className={`w-full py-2 px-4 rounded-md transition-colors ${\n            isAdded\n              ? ''bg-green-600 text-white''\n              : ''bg-blue-600 text-white hover:bg-blue-700''\n          }`}\n        >\n          {isAdded ? ''Added to Cart!'' : ''Add to Cart''}\n        </button>\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Pricing Table',
  'Three-tier pricing comparison table with features and call-to-action buttons',
  'ecommerce',
  'react',
  '{"files": [{"path": "PricingTable.tsx", "content": "export default function PricingTable() {\n  const plans = [\n    {\n      name: ''Basic'',\n      price: ''$9'',\n      features: [''10 Projects'', ''5GB Storage'', ''Email Support'', ''Basic Analytics''],\n      highlighted: false\n    },\n    {\n      name: ''Pro'',\n      price: ''$29'',\n      features: [''Unlimited Projects'', ''50GB Storage'', ''Priority Support'', ''Advanced Analytics'', ''Custom Domain''],\n      highlighted: true\n    },\n    {\n      name: ''Enterprise'',\n      price: ''$99'',\n      features: [''Unlimited Everything'', ''500GB Storage'', ''24/7 Phone Support'', ''Custom Analytics'', ''Dedicated Manager'', ''SLA Guarantee''],\n      highlighted: false\n    }\n  ];\n\n  return (\n    <div className=\"w-full max-w-6xl mx-auto p-6\">\n      <div className=\"text-center mb-12\">\n        <h2 className=\"text-3xl font-bold text-gray-900 mb-4\">Choose Your Plan</h2>\n        <p className=\"text-gray-600\">Select the perfect plan for your needs</p>\n      </div>\n      <div className=\"grid grid-cols-1 md:grid-cols-3 gap-8\">\n        {plans.map((plan) => (\n          <div\n            key={plan.name}\n            className={`rounded-lg p-6 ${\n              plan.highlighted\n                ? ''bg-blue-600 text-white shadow-xl scale-105''\n                : ''bg-white text-gray-900 shadow-md''\n            }`}\n          >\n            <h3 className=\"text-2xl font-bold mb-2\">{plan.name}</h3>\n            <div className=\"mb-6\">\n              <span className=\"text-4xl font-bold\">{plan.price}</span>\n              <span className={plan.highlighted ? ''text-blue-100'' : ''text-gray-600''}>/month</span>\n            </div>\n            <ul className=\"space-y-3 mb-6\">\n              {plan.features.map((feature, index) => (\n                <li key={index} className=\"flex items-center\">\n                  <svg className=\"w-5 h-5 mr-2 flex-shrink-0\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                    <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M5 13l4 4L19 7\" />\n                  </svg>\n                  <span>{feature}</span>\n                </li>\n              ))}\n            </ul>\n            <button\n              className={`w-full py-2 px-4 rounded-md font-semibold transition-colors ${\n                plan.highlighted\n                  ? ''bg-white text-blue-600 hover:bg-gray-100''\n                  : ''bg-blue-600 text-white hover:bg-blue-700''\n              }`}\n            >\n              Get Started\n            </button>\n          </div>\n        ))}\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Shopping Cart',
  'Shopping cart with item list, quantity controls, subtotal, and checkout button',
  'ecommerce',
  'react',
  '{"files": [{"path": "ShoppingCart.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\ntype CartItem = {\n  id: number;\n  name: string;\n  price: number;\n  quantity: number;\n};\n\nexport default function ShoppingCart() {\n  const [items, setItems] = useState<CartItem[]>([\n    { id: 1, name: ''Wireless Headphones'', price: 79.99, quantity: 1 },\n    { id: 2, name: ''Smart Watch'', price: 199.99, quantity: 2 },\n    { id: 3, name: ''USB-C Cable'', price: 12.99, quantity: 3 }\n  ]);\n\n  const updateQuantity = (id: number, delta: number) => {\n    setItems(items.map(item =>\n      item.id === id ? { ...item, quantity: Math.max(0, item.quantity + delta) } : item\n    ).filter(item => item.quantity > 0));\n  };\n\n  const subtotal = items.reduce((sum, item) => sum + item.price * item.quantity, 0);\n  const tax = subtotal * 0.1;\n  const total = subtotal + tax;\n\n  return (\n    <div className=\"w-full max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-6\">Shopping Cart</h2>\n      <div className=\"space-y-4\">\n        {items.map(item => (\n          <div key={item.id} className=\"flex items-center justify-between p-4 border border-gray-200 rounded-md\">\n            <div className=\"flex-1\">\n              <h3 className=\"font-semibold text-gray-900\">{item.name}</h3>\n              <p className=\"text-gray-600\">${item.price.toFixed(2)}</p>\n            </div>\n            <div className=\"flex items-center space-x-3\">\n              <button\n                onClick={() => updateQuantity(item.id, -1)}\n                className=\"w-8 h-8 rounded-full border border-gray-300 hover:bg-gray-100\"\n              >\n                -\n              </button>\n              <span className=\"w-8 text-center font-semibold\">{item.quantity}</span>\n              <button\n                onClick={() => updateQuantity(item.id, 1)}\n                className=\"w-8 h-8 rounded-full border border-gray-300 hover:bg-gray-100\"\n              >\n                +\n              </button>\n            </div>\n            <div className=\"ml-6 font-semibold text-gray-900 w-20 text-right\">\n              ${(item.price * item.quantity).toFixed(2)}\n            </div>\n          </div>\n        ))}\n      </div>\n      <div className=\"mt-6 border-t border-gray-200 pt-4 space-y-2\">\n        <div className=\"flex justify-between text-gray-600\">\n          <span>Subtotal</span>\n          <span>${subtotal.toFixed(2)}</span>\n        </div>\n        <div className=\"flex justify-between text-gray-600\">\n          <span>Tax (10%)</span>\n          <span>${tax.toFixed(2)}</span>\n        </div>\n        <div className=\"flex justify-between text-xl font-bold text-gray-900 pt-2 border-t border-gray-200\">\n          <span>Total</span>\n          <span>${total.toFixed(2)}</span>\n        </div>\n      </div>\n      <button className=\"w-full mt-6 bg-blue-600 text-white py-3 px-4 rounded-md hover:bg-blue-700 transition-colors font-semibold\">\n        Proceed to Checkout\n      </button>\n    </div>\n  );\n}"}]}',
  true,
  null
);

