INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Toast Notification',
  'Dismissible toast notification with success, error, warning, and info variants',
  'other',
  'react',
  '{"files": [{"path": "ToastNotification.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\ntype ToastType = ''success'' | ''error'' | ''warning'' | ''info'';\n\nexport default function ToastNotification() {\n  const [toasts, setToasts] = useState<{ id: number; type: ToastType; message: string }[]>([]);\n  const [nextId, setNextId] = useState(1);\n\n  const addToast = (type: ToastType, message: string) => {\n    const id = nextId;\n    setNextId(id + 1);\n    setToasts([...toasts, { id, type, message }]);\n    setTimeout(() => removeToast(id), 5000);\n  };\n\n  const removeToast = (id: number) => {\n    setToasts(toasts.filter(t => t.id !== id));\n  };\n\n  const getToastStyles = (type: ToastType) => {\n    const styles = {\n      success: ''bg-green-50 border-green-200 text-green-800'',\n      error: ''bg-red-50 border-red-200 text-red-800'',\n      warning: ''bg-yellow-50 border-yellow-200 text-yellow-800'',\n      info: ''bg-blue-50 border-blue-200 text-blue-800''\n    };\n    return styles[type];\n  };\n\n  return (\n    <div className=\"p-6\">\n      <div className=\"space-x-2 mb-4\">\n        <button onClick={() => addToast(''success'', ''Operation completed successfully!'')} className=\"px-4 py-2 bg-green-600 text-white rounded-md\">Success</button>\n        <button onClick={() => addToast(''error'', ''An error occurred. Please try again.'')} className=\"px-4 py-2 bg-red-600 text-white rounded-md\">Error</button>\n        <button onClick={() => addToast(''warning'', ''Warning: This action cannot be undone.'')} className=\"px-4 py-2 bg-yellow-600 text-white rounded-md\">Warning</button>\n        <button onClick={() => addToast(''info'', ''New updates are available.'')} className=\"px-4 py-2 bg-blue-600 text-white rounded-md\">Info</button>\n      </div>\n      <div className=\"fixed top-4 right-4 space-y-2 z-50\">\n        {toasts.map(toast => (\n          <div\n            key={toast.id}\n            className={`flex items-center justify-between p-4 border rounded-md shadow-lg min-w-[300px] ${getToastStyles(toast.type)}`}\n          >\n            <span>{toast.message}</span>\n            <button\n              onClick={() => removeToast(toast.id)}\n              className=\"ml-4 hover:opacity-70\"\n            >\n              <svg className=\"w-5 h-5\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M6 18L18 6M6 6l12 12\" />\n              </svg>\n            </button>\n          </div>\n        ))}\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

