  'dashboard',
  'react',
  '{"files": [{"path": "DataTable.tsx", "content": "''use client'';\n\nimport { useState } from ''react'';\n\ntype User = { id: number; name: string; email: string; role: string; status: string };\n\nexport default function DataTable() {\n  const [data] = useState<User[]>([\n    { id: 1, name: ''John Doe'', email: ''john@example.com'', role: ''Admin'', status: ''Active'' },\n    { id: 2, name: ''Jane Smith'', email: ''jane@example.com'', role: ''Editor'', status: ''Active'' },\n    { id: 3, name: ''Bob Johnson'', email: ''bob@example.com'', role: ''Viewer'', status: ''Inactive'' },\n    { id: 4, name: ''Alice Brown'', email: ''alice@example.com'', role: ''Editor'', status: ''Active'' },\n    { id: 5, name: ''Charlie Wilson'', email: ''charlie@example.com'', role: ''Viewer'', status: ''Active'' }\n  ]);\n  const [currentPage, setCurrentPage] = useState(1);\n  const itemsPerPage = 3;\n  const totalPages = Math.ceil(data.length / itemsPerPage);\n  const startIndex = (currentPage - 1) * itemsPerPage;\n  const currentData = data.slice(startIndex, startIndex + itemsPerPage);\n\n  return (\n    <div className=\"w-full max-w-6xl mx-auto p-6 bg-white rounded-lg shadow-md\">\n      <div className=\"mb-4 flex justify-between items-center\">\n        <h2 className=\"text-2xl font-bold text-gray-900\">Users</h2>\n        <button className=\"px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700\">Add User</button>\n      </div>\n      <div className=\"overflow-x-auto\">\n        <table className=\"w-full\">\n          <thead className=\"bg-gray-50 border-b border-gray-200\">\n            <tr>\n              <th className=\"px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider\">Name</th>\n              <th className=\"px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider\">Email</th>\n              <th className=\"px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider\">Role</th>\n              <th className=\"px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider\">Status</th>\n              <th className=\"px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider\">Actions</th>\n            </tr>\n          </thead>\n          <tbody className=\"bg-white divide-y divide-gray-200\">\n            {currentData.map(user => (\n              <tr key={user.id} className=\"hover:bg-gray-50\">\n                <td className=\"px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900\">{user.name}</td>\n                <td className=\"px-6 py-4 whitespace-nowrap text-sm text-gray-600\">{user.email}</td>\n                <td className=\"px-6 py-4 whitespace-nowrap text-sm text-gray-600\">{user.role}</td>\n                <td className=\"px-6 py-4 whitespace-nowrap\">\n                  <span className={`px-2 py-1 text-xs rounded-full ${\n                    user.status === ''Active'' ? ''bg-green-100 text-green-800'' : ''bg-gray-100 text-gray-800''\n                  }`}>{user.status}</span>\n                </td>\n                <td className=\"px-6 py-4 whitespace-nowrap text-sm\">\n                  <button className=\"text-blue-600 hover:text-blue-800 mr-3\">Edit</button>\n                  <button className=\"text-red-600 hover:text-red-800\">Delete</button>\n                </td>\n              </tr>\n            ))}\n          </tbody>\n        </table>\n      </div>\n      <div className=\"mt-4 flex items-center justify-between\">\n        <div className=\"text-sm text-gray-600\">\n          Showing {startIndex + 1} to {Math.min(startIndex + itemsPerPage, data.length)} of {data.length} entries\n        </div>\n        <div className=\"flex space-x-2\">\n          <button\n            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}\n            disabled={currentPage === 1}\n            className=\"px-3 py-1 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50\"\n          >\n            Previous\n          </button>\n          {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (\n            <button\n              key={page}\n              onClick={() => setCurrentPage(page)}\n              className={`px-3 py-1 border rounded-md ${\n                currentPage === page ? ''bg-blue-600 text-white'' : ''border-gray-300 hover:bg-gray-50''\n              }`}\n            >\n              {page}\n            </button>\n          ))}\n          <button\n            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}\n            disabled={currentPage === totalPages}\n            className=\"px-3 py-1 border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50\"\n          >\n            Next\n          </button>\n        </div>\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Stats Cards',
  'Dashboard statistics cards with icons, values, and change indicators',
  'dashboard',
  'react',
  '{"files": [{"path": "StatsCards.tsx", "content": "export default function StatsCards() {\n  const stats = [\n    {\n      title: ''Total Revenue'',\n      value: ''$45,231'',\n      change: ''+12.5%'',\n      trend: ''up'',\n      icon: ''M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z''\n    },\n    {\n      title: ''New Users'',\n      value: ''2,345'',\n      change: ''+8.2%'',\n      trend: ''up'',\n      icon: ''M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z''\n    },\n    {\n      title: ''Active Projects'',\n      value: ''127'',\n      change: ''-3.1%'',\n      trend: ''down'',\n      icon: ''M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z''\n    },\n    {\n      title: ''Conversion Rate'',\n      value: ''3.65%'',\n      change: ''+0.3%'',\n      trend: ''up'',\n      icon: ''M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z''\n    }\n  ];\n\n  return (\n    <div className=\"w-full max-w-7xl mx-auto p-6\">\n      <h2 className=\"text-2xl font-bold text-gray-900 mb-6\">Dashboard Overview</h2>\n      <div className=\"grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6\">\n        {stats.map((stat, index) => (\n          <div key={index} className=\"bg-white p-6 rounded-lg shadow-md\">\n            <div className=\"flex items-center justify-between mb-4\">\n              <div className=\"w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center\">\n                <svg className=\"w-6 h-6 text-blue-600\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                  <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d={stat.icon} />\n                </svg>\n              </div>\n              <div className={`flex items-center text-sm font-semibold ${\n                stat.trend === ''up'' ? ''text-green-600'' : ''text-red-600''\n              }`}>\n                <svg className={`w-4 h-4 mr-1 ${stat.trend === ''down'' ? ''rotate-180'' : ''''}`} fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n                  <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M5 10l7-7m0 0l7 7m-7-7v18\" />\n                </svg>\n                {stat.change}\n              </div>\n            </div>\n            <h3 className=\"text-sm text-gray-600 mb-1\">{stat.title}</h3>\n            <p className=\"text-2xl font-bold text-gray-900\">{stat.value}</p>\n          </div>\n        ))}\n      </div>\n    </div>\n  );\n}"}]}',
  true,
  null
);

