INSERT INTO templates (name, description, category, framework, code, is_official, created_by)
VALUES (
  'Breadcrumbs',
  'Breadcrumb navigation component with icons and separators',
  'admin',
  'react',
  '{"files": [{"path": "Breadcrumbs.tsx", "content": "export default function Breadcrumbs() {\n  const breadcrumbs = [\n    { label: ''Home'', href: ''#'', icon: true },\n    { label: ''Dashboard'', href: ''#'' },\n    { label: ''Projects'', href: ''#'' },\n    { label: ''Project Details'', href: ''#'', current: true }\n  ];\n\n  return (\n    <nav className=\"flex items-center space-x-2 p-4 bg-white rounded-lg shadow-sm\">\n      {breadcrumbs.map((crumb, index) => (\n        <div key={index} className=\"flex items-center\">\n          {crumb.icon && (\n            <svg className=\"w-4 h-4 text-gray-500 mr-2\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n              <path\n                strokeLinecap=\"round\"\n                strokeLinejoin=\"round\"\n                strokeWidth={2}\n                d=\"M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6\"\n              />\n            </svg>\n          )}\n          <a\n            href={crumb.href}\n            className={`${\n              crumb.current\n                ? ''text-blue-600 font-semibold''\n                : ''text-gray-600 hover:text-blue-600''\n            } transition-colors`}\n          >\n            {crumb.label}\n          </a>\n          {index < breadcrumbs.length - 1 && (\n            <svg className=\"w-4 h-4 text-gray-400 mx-2\" fill=\"none\" stroke=\"currentColor\" viewBox=\"0 0 24 24\">\n              <path strokeLinecap=\"round\" strokeLinejoin=\"round\" strokeWidth={2} d=\"M9 5l7 7-7 7\" />\n            </svg>\n          )}\n        </div>\n      ))}\n    </nav>\n  );\n}"}]}',
  true,
  null
);
